mapper.select:
SELECT TBI.id, TBI.logoFileId, TBI.valid, TBI.aliases, TBI.code, TBI.createDate, TBI.institutionLoginUrl, TBI.lastUpdateDate, TBI.logoUrl, TBI.modifyAble, TBI.name, TBI.personalLoginUrl, TBI.remark FROM BD_BANKINFO TBI WHERE
AND TBI.ID = #{id}
AND TBI.code = #{code}

	SELECT 
			TBI.id,
			TBI.logoFileId,
			TBI.valid,
			TBI.aliases,
			TBI.code,
			TBI.createDate,
			TBI.institutionLoginUrl,
			TBI.lastUpdateDate,
			TBI.logoUrl,
			TBI.modifyAble,
			TBI.name,
			TBI.personalLoginUrl,
			TBI.remark
	  FROM BD_BANKINFO TBI
	
		  
             AND TBI.MODIFYABLE = #{modifyAble} 
        
		  
             AND TBI.VALID = #{valid} 
        
		  
             AND TBI.CODE = #{code} 
        
		  
             AND TBI.ALIASES = #{aliases} 
        
		  
             AND TBI.NAME = #{name} 
        
		
			 AND TBI.ID <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM BD_BANKINFO TBI
	
		  
             AND TBI.MODIFYABLE = #{modifyAble} 
        
		  
             AND TBI.VALID = #{valid} 
        
		  
             AND TBI.CODE = #{code} 
        
		  
             AND TBI.ALIASES = #{aliases} 
        
		  
             AND TBI.NAME = #{name} 
        
		
			 AND TBI.ID <> #{excludeId}
