mapper.select:
	SELECT 
			TI.ID,
			TI.VALID,
			TI.PARENTID,
			TI.BASEPARENTID,
			TI.NAME,
			TI.CODE,
			TI.CREATEDATE,
			TI.LASTUPDATEDATE
	  FROM BD_INDUSTRY TI
	
		  
            AND TI.ID = #{id,javaType=java.lang.String}
        
		  
            AND TI.VALID = #{valid,javaType=boolean}
        
        
    		
    			
    				AND TI.PARENTID = #{parentId,javaType=java.lang.String}
    			
    			
    				AND TI.PARENTID IS NULL
    			
    		
    	
    	
    		AND TI.PARENTID IS NULL
    	
    	
    		
    			
    				AND TI.BASEPARENTID = #{baseParentId,javaType=java.lang.String}
    			
    			
    				AND TI.BASEPARENTID IS NULL
    			
    		
    	
          
            AND TI.BASEPARENTID = #{baseParentId,javaType=java.lang.String}
        
		  
            AND TI.REMARK = #{remark,javaType=java.lang.String}
        
		  
            AND TI.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TI.CODE = #{code,javaType=java.lang.String}
        
		  
            AND TI.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TI.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM BD_INDUSTRY TI
	
		  
            AND TI.ID = #{id,javaType=java.lang.String}
        
		  
            AND TI.VALID = #{valid,javaType=boolean}
        
		  
            AND TI.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TI.BASEPARENTID = #{baseParentId,javaType=java.lang.String}
        
		  
            AND TI.REMARK = #{remark,javaType=java.lang.String}
        
		  
            AND TI.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TI.CODE = #{code,javaType=java.lang.String}
        
		  
            AND TI.CREATEDATE = #{createDate,javaType=java.util.Date}
        
		  
            AND TI.LASTUPDATEDATE = #{lastUpdateDate,javaType=java.util.Date}
