mapper.select:
	SELECT 
			TIAR.id,
			TIAR.valid,
			TIAR.cityName,
			TIAR.code,
			TIAR.continentName,
			TIAR.countyName,
			TIAR.createDate,
			TIAR.districtId AS district_id,
			TIAR.en,
			TIAR.enName,
			TIAR.lastUpdateDate,
			TIAR.latitude,
			TIAR.longitude,
			TIAR.modifyAble,
			TIAR.name,
			TIAR.nationName,
			TIAR.provinceName,
			TIAR.remark,
			TIAR.telecomOperatorId AS telecomOperator_id,
			TIAR.version
	  FROM BD_IPADDRESS_REPOSITYTIAR
	
		  
             AND TIAR.VALID = #{valid} 
        
		  
             AND TIAR.CONTINENTNAME = #{continentName} 
        
		  
             AND TIAR.COUNTYNAME = #{countyName} 
        
		  
             AND TIAR.ENNAME = #{enName} 
        
		  
             AND TIAR.NATIONNAME = #{nationName} 
        
		  
             AND TIAR.CITYNAME = #{cityName} 
        
		  
             AND TIAR.NAME = #{name} 
        
		  
             AND TIAR.MODIFYABLE = #{modifyAble} 
        
		  
             AND TIAR.PROVINCENAME = #{provinceName} 
        
		  
             AND TIAR.EN = #{en} 
        
		  
             AND TIAR.CODE = #{code} 
        
		  
             AND TIAR.CREATEDATE < #{maxCreateDate} 
        
		  
             AND TIAR.CREATEDATE >= #{minCreateDate} 
        
		
			 AND TIAR.ID <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM BD_IPADDRESS_REPOSITYTIAR
	
		  
             AND TIAR.VALID = #{valid} 
        
		  
             AND TIAR.CONTINENTNAME = #{continentName} 
        
		  
             AND TIAR.COUNTYNAME = #{countyName} 
        
		  
             AND TIAR.ENNAME = #{enName} 
        
		  
             AND TIAR.NATIONNAME = #{nationName} 
        
		  
             AND TIAR.CITYNAME = #{cityName} 
        
		  
             AND TIAR.NAME = #{name} 
        
		  
             AND TIAR.MODIFYABLE = #{modifyAble} 
        
		  
             AND TIAR.PROVINCENAME = #{provinceName} 
        
		  
             AND TIAR.EN = #{en} 
        
		  
             AND TIAR.CODE = #{code} 
        
		  
             AND TIAR.CREATEDATE < #{maxCreateDate} 
        
		  
             AND TIAR.CREATEDATE >= #{minCreateDate} 
        
		
			 AND TIAR.ID <> #{excludeId}
