SELECT TO_.ID, TO_.VCID, TO_.MAINPOSTID AS mainPost_id, TO_.ORGANIZATIONID AS organization_id, TO_.VALID, TO_.PWDERRCOUNT, TO_.HISTORYPWD, TO_.INVALIDDATE, TO_.LASTUPDATEDATE, TO_.PWDUPDATEDATE, TO_.USERNAME, TO_.LOCKED, TO_.CREATEDATE, TO_.EXAMINEPWD, TO_.LOGINNAME, TO_.PASSWORD FROM OPER_OPERATOR TO_ WHERE
AND TO_.ID = #{id}
AND TO_.LOGINNAME = #{loginName}
AND TO_.PASSWORD = #{password}

