SELECT TO_.ID, TO_.VALID, TO_.VCID, TO_.FULLADDRESS, TO_.ALIAS, TO_.REMARK, TO_.CODE, TO_.TYPE, TO_.PARENTID, TO_.DISTRICTID, TO_.ADDRESS, TO_.NAME, TO_.COMPANYID AS company_id, TO_.FULLNAME, TO_.CHIEFTYPE, TO_.CHIEFID FROM OPER_ORGANIZATION TO_ WHERE
AND TO_.ID = #{id}
AND TO_.VCID = #{vcid}
AND TO_.CODE = #{code}

	SELECT 
			TO_.ID,
			TO_.VALID,
			TO_.VCID,
			TO_.FULLADDRESS,
			TO_.ALIAS,
			TO_.REMARK,
			TO_.CODE,
			TO_.TYPE,
			TO_.PARENTID,
			TO_.DISTRICTID,
			TO_.ADDRESS,
			TO_.CHIEFTYPE,
			TO_.NAME,
			TO_.FULLNAME,
			TO_.COMPANYID AS company_id,
			TO_.CHIEFID
	  FROM OPER_ORGANIZATION TO_
	
		  
            AND TO_.ID = #{id,javaType=java.lang.String}
        
		
			 AND TO_.ID <> #{excludeOrganizationId,javaType=java.lang.String} 
		
       	  
            AND TO_.COMPANYID = #{companyId,javaType=java.lang.String}
        			
       	  
            AND TO_.COMPANYID = #{company.id,javaType=java.lang.String}
        						
       	  
            AND TO_.CODE = #{code,javaType=java.lang.String}
        
          
            AND TO_.VALID = #{valid,javaType=boolean}
        
          
            AND TO_.TYPE = #{type,javaType=com.tx.component.operator.model.OrganizationTypeEnum}
        
          
            AND TO_.TYPE IN
            #{organizationTypeTemp,javaType=com.tx.component.operator.model.OrganizationTypeEnum}
        	        
		  
            AND TO_.PARENTID = #{parentId,javaType=java.lang.String}
        
          
            AND TO_.DISTRICTID = #{districtId,javaType=java.lang.String}
        
          
            AND TO_.CHIEFTYPE = #{chiefType,javaType=com.tx.component.operator.model.ChiefTypeEnum}
        
          
            AND TO_.CHIEFID = #{chiefId,javaType=java.lang.String}
        
        
        	
        		AND TO_.VCID = #{vcid,javaType=java.lang.String}
        	
        	
        		
	               AND TO_.VCID IN #{refId}
	           
	           
                   AND TO_.VCID = ''
               
        	
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY id

	SELECT COUNT(1)
	  FROM OPER_ORGANIZATION TO_
	
		  
            AND TO_.ID = #{id,javaType=java.lang.String}
        
		
			 AND TO_.ID <> #{excludeOrganizationId,javaType=java.lang.String} 
		
       	  
            AND TO_.CODE = #{code,javaType=java.lang.String}
        
          
            AND TO_.VALID = #{valid,javaType=boolean}
        
       	  
            AND TO_.COMPANYID = #{companyId,javaType=java.lang.String}
        			
       	  
            AND TO_.COMPANYID = #{company.id,javaType=java.lang.String}
        				        
          
            AND TO_.TYPE = #{type,javaType=com.tx.component.operator.model.OrganizationTypeEnum}
        
          
            AND TO_.TYPE IN
            #{organizationTypeTemp,javaType=com.tx.component.operator.model.OrganizationTypeEnum}
        	        
		  
            AND TO_.PARENTID = #{parentId,javaType=java.lang.String}
        
          
            AND TO_.DISTRICTID = #{districtId,javaType=java.lang.String}
        
          
            AND TO_.CHIEFTYPE = #{chiefType,javaType=com.tx.component.operator.model.ChiefTypeEnum}
        
          
            AND TO_.CHIEFID = #{chiefId,javaType=java.lang.String}
        
        
        	
        		AND TO_.VCID = #{vcid,javaType=java.lang.String}
        	
        	
        		
	               AND TO_.VCID IN #{refId}
	           
	           
                   AND TO_.VCID = ''
