mapper.select:
	SELECT 
			TP.ID,
			TP.VCID,
			TP.VALID,
			TP.PARENTID,
			TP.ORGANIZATIONID AS organization_id,
			TP.REMARK,
			TP.NAME,
			TP.FULLNAME,
			TP.CODE
	  FROM OPER_POST TP
	
		  
            AND TP.ID = #{id,javaType=java.lang.String}
        
        
			 AND TP.ID <> #{excludePostId,javaType=java.lang.String} 
		
		  
            AND TP.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TP.CODE = #{code,javaType=java.lang.String}
        
          
            AND TP.VALID = #{valid,javaType=java.lang.Boolean}
        
        
			AND TP.ORGANIZATIONID = #{organizationId,javaType=java.lang.String}
		
		
			
				AND TP.VCID = #{vcid,javaType=java.lang.String}
			
			  
	           
	               AND TP.vcid IN #{refId}
	           
	           
                   AND TP.vcid = ''
               
            
		
	
	  
          
            ORDER BY #{orderSql}

	SELECT COUNT(1)
	  FROM OPER_POST TP
	
		  
            AND TP.ID = #{id,javaType=java.lang.String}
        
        
			 AND TP.ID <> #{excludePostId,javaType=java.lang.String} 
		
		  
            AND TP.PARENTID = #{parentId,javaType=java.lang.String}
        
		  
            AND TP.CODE = #{code,javaType=java.lang.String}
        
          
            AND TP.VALID = #{valid,javaType=java.lang.Boolean}
        
        
			AND TP.ORGANIZATIONID = #{organizationId,javaType=java.lang.String}
		
		
			
				AND TP.VCID = #{vcid,javaType=java.lang.String}
			
			  
	           
	               AND TP.vcid IN #{refId}
	           
	           
                   AND TP.vcid = ''
