SELECT TR.ID, TR.VALID, TR.ROLEKEY, TR.REMARK, TR.EDITABLE, TR.NAME, TR.VCID, TR.ROLETYPE, TR.CODE FROM OPER_ROLE TR WHERE
AND TR.ID = #{id}
AND TR.ROLEKEY = #{roleKey}

	SELECT 
			TR.ID,
			TR.VALID,
			TR.ROLEKEY,
			TR.REMARK,
			TR.EDITABLE,
			TR.NAME,
			TR.VCID,
			TR.ROLETYPE,
			TR.CODE
	  FROM OPER_ROLE TR
	
		  
            AND TR.ID = #{id,javaType=java.lang.String}
        
        
			 AND TR.ID <> #{excludeRoleId,javaType=java.lang.String} 
		
		  
            AND TR.VALID = #{valid,javaType=boolean}
        
          
            AND TR.EDITABLE = #{editAble,javaType=boolean}
        
		  
            AND TR.NAME = #{name,javaType=java.lang.String}
        
		  
            AND TR.ROLETYPE = #{roleType,javaType=com.tx.component.operator.model.RoleTypeEnum}
        
          
            AND TR.ROLEKEY = #{roleKey,javaType=com.tx.component.operator.model.RoleEnum}
        
		  
            AND TR.CODE = #{code,javaType=java.lang.String}
        
        
        	
        		AND TR.VCID = #{vcid,javaType=java.lang.String}
        	
        	
        		
	               AND TR.VCID IN #{refId}
	           
	           
                   AND TR.VCID = ''
               
        	
        
	
	  
          
            ORDER BY #{orderSql}
        
          
            ORDER BY ID

	SELECT COUNT(1)
	  FROM OPER_ROLE TR
	
		  
            AND TR.ID = #{id,javaType=java.lang.String}
        
        
			 AND TR.ID <> #{excludeRoleId,javaType=java.lang.String} 
		
		  
            AND TR.VALID = #{valid,javaType=boolean}
        
          
