mapper.select:
SELECT TD.id, TD.code, TD.type, TD.level, TD.parentId AS parent_id, TD.provinceId AS province_id, TD.cityId AS city_id, TD.countyId AS county_id, TD.zipCode, TD.name, TD.pinyin, TD.py, TD.fullName, TD.createDate, TD.lastUpdateDate, TD.valid, TD.modifyAble, TD.remark FROM BD_DISTRICT TD WHERE
AND TD.ID = #{id}
AND TD.CODE = #{code}

	SELECT 
			TD.id,
			TD.code,
			TD.type,
			TD.level,
			TD.parentId AS parent_id,
			TD.provinceId AS province_id,
			TD.cityId AS city_id,
			TD.countyId AS county_id,
			TD.zipCode,
			TD.name,
			TD.pinyin,
			TD.py,
			TD.fullName,
			TD.createDate,
			TD.lastUpdateDate,
			TD.valid,
			TD.modifyAble,
			TD.remark
	  FROM BD_DISTRICT TD
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		
        	
        		 AND TD.PARENTID = #{parentId,jdbcType=VARCHAR} 
        	
        	
        		
        			  
			        	 AND TD.PARENTID = #{parent.id,jdbcType=VARCHAR} 
			        
	        	
        	
        
		  
             AND TD.VALID = #{valid} 
        
		
             AND TD.PARENTID like concat(#{parentIdLike},'%' )
        
          
             AND TD.MODIFYABLE = #{modifyAble} 
        
		  
             AND TD.CODE = #{code} 
        
          
             AND TD.ZIPCODE = #{zipCode} 
        
		  
             AND TD.TYPE = #{type} 
        
          
             AND TD.LEVEL <= #{maxLevel} 
        
		  
             AND TD.LEVEL = #{level} 
        
		  
             AND TD.NAME = #{name} 
        
          
             AND TD.PINYIN LIKE concat(#{pinyin}, '%') 
        
          
             AND TD.PY = #{py} 
        
          
             AND TD.FULLNAME = #{fullName} 
        
          
             AND TD.CREATEDATE >= #{minCreateDate} 
        
		  
             AND TD.CREATEDATE < #{maxCreateDate} 
        
		
			 AND TD.ID <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM BD_DISTRICT TD
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		
        	
        		 AND TD.PARENTID = #{parentId,jdbcType=VARCHAR} 
        	
        	
        		
        			  
			        	 AND TD.PARENTID = #{parent.id,jdbcType=VARCHAR} 
			        
	        	
        	
        
		  
             AND TD.VALID = #{valid} 
        
          
             AND TD.MODIFYABLE = #{modifyAble} 
        
		  
             AND TD.CODE = #{code} 
        
          
             AND TD.ZIPCODE = #{zipCode} 
        
		  
             AND TD.TYPE = #{type} 
        
          
             AND TD.LEVEL <= #{maxLevel} 
        
		  
             AND TD.LEVEL = #{level} 
        
		  
             AND TD.NAME = #{name} 
        
          
             AND TD.PINYIN LIKE concat(#{pinyin}, '%') 
        
          
             AND TD.PY = #{py} 
        
          
             AND TD.FULLNAME = #{fullName} 
        
          
             AND TD.CREATEDATE >= #{minCreateDate} 
        
		  
             AND TD.CREATEDATE < #{maxCreateDate} 
        
		
			 AND TD.ID <> #{excludeId}

mapper.delete:
	DELETE FROM BD_DISTRICT WHERE
	
		  
            AND ID = #{id}
        
          
            AND CODE = #{code}
        
          
            AND PARENTID = #{parent.id}
