mapper.select:
SELECT D.id, D.code, D.py, D.zipCode, D.cityId, D.fullName, D.lastUpdateDate, D.name, D.type, D.level, D.valid, D.countyId, D.createDate, D.modifyAble, D.parentId, D.pinyin, D.remark, D.provinceId, D.attributes FROM BD_DISTRICT D WHERE
AND D.id = #{id}
AND D.code = #{code}

	SELECT 
			D.id,
			D.code,
			D.py,
			D.zipCode,
			D.cityId,
			D.fullName,
			D.lastUpdateDate,
			D.name,
			D.type,
			D.level,
			D.valid,
			D.countyId,
			D.createDate,
			D.modifyAble,
			D.parentId,
			D.pinyin,
			D.remark,
			D.provinceId,
			D.attributes
	  FROM BD_DISTRICT D
	 WHERE
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		
        	
        		 AND D.PARENTID = #{parentId,jdbcType=VARCHAR} 
        	
        	
        		
        			  
			        	 AND D.PARENTID = #{parent.id,jdbcType=VARCHAR} 
			        
	        	
        	
        
          
            AND D.parentId IN #{parentIdTemp}
        
		  
             AND D.VALID = #{valid} 
        
		
             AND D.PARENTID like concat(#{parentIdLike},'%' )
        
          
             AND D.MODIFYABLE = #{modifyAble} 
        
		  
             AND D.CODE = #{code} 
        
          
             AND D.ZIPCODE = #{zipCode} 
        
		  
             AND D.TYPE = #{type} 
        
          
             AND D.LEVEL <= #{maxLevel} 
        
		  
             AND D.LEVEL = #{level} 
        
		  
             AND D.NAME = #{name} 
        
          
             AND D.PINYIN LIKE concat(#{pinyin}, '%') 
        
          
             AND D.PY = #{py} 
        
          
             AND D.FULLNAME = #{fullName} 
        
          
             AND D.CREATEDATE >= #{minCreateDate} 
        
		  
             AND D.CREATEDATE < #{maxCreateDate} 
        
		
			 AND D.ID <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM BD_DISTRICT D
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		
        	
        		 AND D.PARENTID = #{parentId,jdbcType=VARCHAR} 
        	
        	
        		
        			  
			        	 AND D.PARENTID = #{parent.id,jdbcType=VARCHAR} 
			        
	        	
        	
        
          
            AND D.parentId IN #{parentIdTemp}
        
		  
             AND D.VALID = #{valid} 
        
		
             AND D.PARENTID like concat(#{parentIdLike},'%' )
        
          
             AND D.MODIFYABLE = #{modifyAble} 
        
		  
             AND D.CODE = #{code} 
        
          
             AND D.ZIPCODE = #{zipCode} 
        
		  
             AND D.TYPE = #{type} 
        
          
             AND D.LEVEL <= #{maxLevel} 
        
		  
             AND D.LEVEL = #{level} 
        
		  
             AND D.NAME = #{name} 
        
          
             AND D.PINYIN LIKE concat(#{pinyin}, '%') 
        
          
             AND D.PY = #{py} 
        
          
             AND D.FULLNAME = #{fullName} 
        
          
             AND D.CREATEDATE >= #{minCreateDate} 
        
		  
             AND D.CREATEDATE < #{maxCreateDate} 
        
		
			 AND D.ID <> #{excludeId}

mapper.delete:
	DELETE FROM BD_DISTRICT WHERE
	
		  
            AND ID = #{id}
        
          
            AND CODE = #{code}
        
          
            AND PARENTID = #{parent.id}
