mapper.select:
	SELECT 
			CEC.id,
			CEC.lastUpdateDate,
			CEC.lastUpdateUserId,
			CEC.name,
			CEC.type,
			CEC.vcid,
			CEC.topicId,
			CEC.topicType,
			CEC.createDate,
			CEC.createUserId,
			CEC.parentId,
			CEC.remark
	  FROM CAL_EVENT_CATALOG CEC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND CEC.parentId IN #{parentIdTemp}
        
		  
             AND CEC.id = #{id} 
        			
		  
             AND CEC.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND CEC.name = #{name} 
        			
		  
             AND CEC.type = #{type} 
        			
		  
             AND CEC.vcid = #{vcid} 
        			
		  
             AND CEC.topicId = #{topicId} 
        			
		  
             AND CEC.topicType = #{topicType} 
        			
		  
             AND CEC.createDate >= #{minCreateDate} 
        
		  
             AND CEC.createDate < #{maxCreateDate} 
        
		  
             AND CEC.createUserId = #{createUserId} 
        			
		  
             AND CEC.parentId = #{parentId} 
        			
		  
             AND CEC.remark = #{remark} 
        			
		
			 AND CEC.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM CAL_EVENT_CATALOG CEC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND CEC.parentId IN #{parentIdTemp}
        
		  
             AND CEC.id = #{id} 
        			
		  
             AND CEC.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND CEC.name = #{name} 
        			
		  
             AND CEC.type = #{type} 
        			
		  
             AND CEC.vcid = #{vcid} 
        			
		  
             AND CEC.topicId = #{topicId} 
        			
		  
             AND CEC.topicType = #{topicType} 
        			
		  
             AND CEC.createDate >= #{minCreateDate} 
        
		  
             AND CEC.createDate < #{maxCreateDate} 
        
		  
             AND CEC.createUserId = #{createUserId} 
        			
		  
             AND CEC.parentId = #{parentId} 
        			
		  
             AND CEC.remark = #{remark} 
        			
		
			 AND CEC.id <> #{excludeId}
