mapper.select:
	SELECT 
			CE.id,
			CE.allDay,
			CE.end,
			CE.url,
			CE.daysOfWeek,
			CE.lastUpdateDate,
			CE.lastUpdateUserId,
			CE.type,
			CE.vcid,
			CE.className,
			CE.rrule,
			CE.start,
			CE.title,
			CE.topicId,
			CE.topicType,
			CE.createDate,
			CE.createUserId,
			CE.remark,
			CE.catalogId,
			CE.creator,
			CE.overlap,
			CE.duration,
			CE.editable,
			CE.attributes
	  FROM CAL_CALENDAR_EVENT CE
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
        
        	AND (
        	
	             (CE.start <= #{betweenStart} BETWEEN AND CE.end >= #{betweenStart}) 
	        
	        	OR
	          
	             (CE.start <= #{betweenEnd} BETWEEN AND CE.end >= #{betweenEnd}) 
	        
			)
        
          
             AND CE.start >= #{minStart} 
        
		  
             AND CE.start < #{maxStart} 
        
          
             AND CE.end >= #{minEnd} 
        
		  
             AND CE.end < #{maxEnd} 
        
		  
             AND CE.id = #{id} 
        			
		  
             AND CE.allDay = #{allDay} 
        			
		  
             AND CE.url = #{url} 
        			
		  
             AND CE.daysOfWeek = #{daysOfWeek} 
        			
		  
             AND CE.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND CE.type = #{type} 
        			
		  
             AND CE.vcid = #{vcid} 
        			
		  
             AND CE.className = #{className} 
        			
		  
             AND CE.rrule = #{rrule} 
        			
		  
             AND CE.title = #{title} 
        			
		  
             AND CE.topicId = #{topicId} 
        			
		  
             AND CE.topicType = #{topicType} 
        			
		  
             AND CE.createDate >= #{minCreateDate} 
        
		  
             AND CE.createDate < #{maxCreateDate} 
        
		  
             AND CE.createUserId = #{createUserId} 
        			
		  
             AND CE.remark = #{remark} 
        			
		
			  
	             AND CE.catalogId = #{catalog.id} 
	        
        			
		  
             AND CE.overlap = #{overlap} 
        			
		  
             AND CE.duration = #{duration} 
        
          
             AND CE.creator = #{creator} 
        		
		  
             AND CE.editable = #{editable} 
        			
		  
             AND CE.attributes = #{attributes} 
        			
		
			 AND CE.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM CAL_CALENDAR_EVENT CE
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
        
        	AND (
        	
	             (CE.start <= #{betweenStart} BETWEEN AND CE.end >= #{betweenStart}) 
	        
	        	OR
	          
	             (CE.start <= #{betweenEnd} BETWEEN AND CE.end >= #{betweenEnd}) 
	        
			)
        
          
             AND CE.start >= #{minStart} 
        
		  
             AND CE.start < #{maxStart} 
        
          
             AND CE.end >= #{minEnd} 
        
		  
             AND CE.end < #{maxEnd} 
        
		  
             AND CE.id = #{id} 
        			
		  
             AND CE.allDay = #{allDay} 
        			
		  
             AND CE.url = #{url} 
        			
		  
             AND CE.daysOfWeek = #{daysOfWeek} 
        			
		  
             AND CE.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND CE.type = #{type} 
        			
		  
             AND CE.vcid = #{vcid} 
        			
		  
             AND CE.className = #{className} 
        			
		  
             AND CE.rrule = #{rrule} 
        			
		  
             AND CE.title = #{title} 
        			
		  
             AND CE.topicId = #{topicId} 
        			
		  
             AND CE.topicType = #{topicType} 
        			
		  
             AND CE.createDate >= #{minCreateDate} 
        
		  
             AND CE.createDate < #{maxCreateDate} 
        
		  
             AND CE.createUserId = #{createUserId} 
        			
		  
             AND CE.remark = #{remark} 
        			
		
			  
	             AND CE.catalogId = #{catalog.id} 
	        
        			
		  
             AND CE.overlap = #{overlap} 
        			
		  
             AND CE.duration = #{duration} 
        
          
             AND CE.creator = #{creator} 
        		
		  
             AND CE.editable = #{editable} 
        			
		  
             AND CE.attributes = #{attributes} 
        			
		
			 AND CE.id <> #{excludeId}
