mapper.select:
SELECT CPC.id, CPC.code, CPC.lastUpdateDate, CPC.name, CPC.valid, CPC.createDate, CPC.modifyAble, CPC.remark FROM CLI_PROMOTION_CHANNEL CPC WHERE
AND CPC.id = #{id}
AND CPC.code = #{code}

	SELECT 
			CPC.id,
			CPC.code,
			CPC.lastUpdateDate,
			CPC.name,
			CPC.valid,
			CPC.createDate,
			CPC.modifyAble,
			CPC.remark
	  FROM CLI_PROMOTION_CHANNEL CPC
	
		  
             AND CPC.id = #{id} 
        
		  
             AND CPC.code = #{code} 
        
		  
             AND CPC.lastUpdateDate = #{lastUpdateDate} 
        
		  
             AND CPC.name = #{name} 
        
		  
             AND CPC.valid = #{valid} 
        
		  
             AND CPC.createDate = #{createDate} 
        
		  
             AND CPC.modifyAble = #{modifyAble} 
        
		  
             AND CPC.remark = #{remark} 
        
		
			 AND CPC.id <> #{excludeId} 
		
	
	ORDER BY id

	SELECT COUNT(1)
	  FROM CLI_PROMOTION_CHANNEL CPC
	
		  
             AND CPC.id = #{id} 
        
		  
             AND CPC.code = #{code} 
        
		  
             AND CPC.lastUpdateDate = #{lastUpdateDate} 
        
		  
             AND CPC.name = #{name} 
        
		  
             AND CPC.valid = #{valid} 
        
		  
             AND CPC.createDate = #{createDate} 
        
		  
             AND CPC.modifyAble = #{modifyAble} 
        
		  
             AND CPC.remark = #{remark} 
        
		
			 AND CPC.id <> #{excludeId}
