mapper.select:
SELECT CS.id, CS.code, CS.lastUpdateDate, CS.name, CS.valid, CS.createDate, CS.modifyAble, CS.remark FROM CLI_CLIENT_SOURCE CS WHERE
AND CS.id = #{id}
AND CS.code = #{code}

	SELECT 
			CS.id,
			CS.code,
			CS.lastUpdateDate,
			CS.name,
			CS.valid,
			CS.createDate,
			CS.modifyAble,
			CS.remark
	  FROM CLI_CLIENT_SOURCE CS
	
		  
             AND CS.id = #{id} 
        
		  
             AND CS.code = #{code} 
        
		  
             AND CS.lastUpdateDate = #{lastUpdateDate} 
        
		  
             AND CS.name = #{name} 
        
		  
             AND CS.valid = #{valid} 
        
		  
             AND CS.createDate = #{createDate} 
        
		  
             AND CS.modifyAble = #{modifyAble} 
        
		  
             AND CS.remark = #{remark} 
        
		
			 AND CS.id <> #{excludeId} 
		
	
	ORDER BY id

	SELECT COUNT(1)
	  FROM CLI_CLIENT_SOURCE CS
	
		  
             AND CS.id = #{id} 
        
		  
             AND CS.code = #{code} 
        
		  
             AND CS.lastUpdateDate = #{lastUpdateDate} 
        
		  
             AND CS.name = #{name} 
        
		  
             AND CS.valid = #{valid} 
        
		  
             AND CS.createDate = #{createDate} 
        
		  
             AND CS.modifyAble = #{modifyAble} 
        
		  
             AND CS.remark = #{remark} 
        
		
			 AND CS.id <> #{excludeId}
