mapper.select:
SELECT CIC.id, CIC.code, CIC.lastUpdateDate, CIC.lastUpdateOperatorId, CIC.name, CIC.typeCode, CIC.level, CIC.orderIndex, CIC.valid, CIC.createDate, CIC.createOperatorId, CIC.modifyAble, CIC.parentId, CIC.remark FROM CI_CONTENT_CATEGORY CIC WHERE
AND CIC.id = #{id}
AND CIC.code = #{code}

	SELECT 
			CIC.id,
			CIC.code,
			CIC.lastUpdateDate,
			CIC.lastUpdateOperatorId,
			CIC.name,
			CIC.typeCode,
			CIC.level,
			CIC.orderIndex,
			CIC.valid,
			CIC.createDate,
			CIC.createOperatorId,
			CIC.modifyAble,
			CIC.parentId,
			CIC.remark
	  FROM CI_CONTENT_CATEGORY CIC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND CIC.id = #{id} 
        			
		  
             AND CIC.code = #{code} 
        			
		  
             AND CIC.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND CIC.name = #{name} 
        			
		
			  
	             AND CIC.typeCode = #{type.code} 
	        
        			
		  
             AND CIC.level = #{level} 
        			
		  
             AND CIC.orderIndex = #{orderIndex} 
        			
		  
             AND CIC.valid = #{valid} 
        			
		  
             AND CIC.createDate >= #{minCreateDate} 
        
		  
             AND CIC.createDate < #{maxCreateDate} 
        
		  
             AND CIC.createOperatorId = #{createOperatorId} 
        			
		  
             AND CIC.modifyAble = #{modifyAble} 
        			
		
			  
	             AND CIC.parentId = #{parent.id} 
	        
        			
		  
             AND CIC.remark = #{remark} 
        			
		
			 AND CIC.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM CI_CONTENT_CATEGORY CIC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND CIC.id = #{id} 
        			
		  
             AND CIC.code = #{code} 
        			
		  
             AND CIC.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND CIC.name = #{name} 
        			
		
			  
	             AND CIC.typeCode = #{type.code} 
	        
        			
		  
             AND CIC.level = #{level} 
        			
		  
             AND CIC.orderIndex = #{orderIndex} 
        			
		  
             AND CIC.valid = #{valid} 
        			
		  
             AND CIC.createDate >= #{minCreateDate} 
        
		  
             AND CIC.createDate < #{maxCreateDate} 
        
		  
             AND CIC.createOperatorId = #{createOperatorId} 
        			
		  
             AND CIC.modifyAble = #{modifyAble} 
        			
		
			  
	             AND CIC.parentId = #{parent.id} 
	        
        			
		  
             AND CIC.remark = #{remark} 
        			
		
			 AND CIC.id <> #{excludeId}

mapper.delete:
	DELETE FROM CI_CONTENT_CATEGORY  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
