mapper.select:
SELECT CIL.id, CIL.code, CIL.lastUpdateDate, CIL.lastUpdateOperatorId, CIL.name, CIL.valid, CIL.createDate, CIL.createOperatorId, CIL.modifyAble, CIL.remark, CIL.categoryCode FROM CI_CONTENT_LEVEL CIL WHERE
AND CIL.id = #{id}
AND CIL.code = #{code}

	SELECT 
			CIL.id,
			CIL.code,
			CIL.lastUpdateDate,
			CIL.lastUpdateOperatorId,
			CIL.name,
			CIL.valid,
			CIL.createDate,
			CIL.createOperatorId,
			CIL.modifyAble,
			CIL.remark,
			CIL.categoryCode
	  FROM CI_CONTENT_LEVEL CIL
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND CIL.id = #{id} 
        			
		  
             AND CIL.code = #{code} 
        			
		  
             AND CIL.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND CIL.name = #{name} 
        			
		  
             AND CIL.valid = #{valid} 
        			
		  
             AND CIL.createDate >= #{minCreateDate} 
        
		  
             AND CIL.createDate < #{maxCreateDate} 
        
		  
             AND CIL.createOperatorId = #{createOperatorId} 
        			
		  
             AND CIL.modifyAble = #{modifyAble} 
        			
		  
             AND CIL.remark = #{remark} 
        			
		
			  
	             AND CIL.categoryCode = #{category.code} 
	        
        			
		
			 AND CIL.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM CI_CONTENT_LEVEL CIL
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND CIL.id = #{id} 
        			
		  
             AND CIL.code = #{code} 
        			
		  
             AND CIL.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND CIL.name = #{name} 
        			
		  
             AND CIL.valid = #{valid} 
        			
		  
             AND CIL.createDate >= #{minCreateDate} 
        
		  
             AND CIL.createDate < #{maxCreateDate} 
        
		  
             AND CIL.createOperatorId = #{createOperatorId} 
        			
		  
             AND CIL.modifyAble = #{modifyAble} 
        			
		  
             AND CIL.remark = #{remark} 
        			
		
			  
	             AND CIL.categoryCode = #{category.code} 
	        
        			
		
			 AND CIL.id <> #{excludeId}

mapper.delete:
	DELETE FROM CI_CONTENT_LEVEL  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
