mapper.select:
	SELECT 
			CI.id,
			CI.fileId,
			CI.fileUrl,
			CI.lastUpdateDate,
			CI.lastUpdateOperatorId,
			CI.linkUrl,
			CI.name,
			CI.typeCode,
			CI.levelCode,
			CI.orderIndex,
			CI.title,
			CI.valid,
			CI.createDate,
			CI.createOperatorId,
			CI.remark,
			CI.content,
			CI.categoryCode,
			CI.keywords
	  FROM CI_CONTENT_INFO CI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND CI.id = #{id} 
        			
		  
             AND CI.fileId = #{fileId} 
        			
		  
             AND CI.fileUrl = #{fileUrl} 
        			
		  
             AND CI.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND CI.linkUrl = #{linkUrl} 
        			
		  
             AND CI.name = #{name} 
        			
		
			  
	             AND CI.typeCode = #{type.code} 
	        
        			
		
			  
	             AND CI.levelCode = #{level.code} 
	        
        			
		  
             AND CI.orderIndex = #{orderIndex} 
        			
		  
             AND CI.title = #{title} 
        			
		  
             AND CI.valid = #{valid} 
        			
		  
             AND CI.createDate >= #{minCreateDate} 
        
		  
             AND CI.createDate < #{maxCreateDate} 
        
		  
             AND CI.createOperatorId = #{createOperatorId} 
        			
		  
             AND CI.remark = #{remark} 
        			
		  
             AND CI.content = #{content} 
        			
		
			  
	             AND CI.categoryCode = #{category.code} 
	        
        			
		  
             AND CI.keywords = #{keywords} 
        			
		
			 AND CI.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM CI_CONTENT_INFO CI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND CI.id = #{id} 
        			
		  
             AND CI.fileId = #{fileId} 
        			
		  
             AND CI.fileUrl = #{fileUrl} 
        			
		  
             AND CI.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND CI.linkUrl = #{linkUrl} 
        			
		  
             AND CI.name = #{name} 
        			
		
			  
	             AND CI.typeCode = #{type.code} 
	        
        			
		
			  
	             AND CI.levelCode = #{level.code} 
	        
        			
		  
             AND CI.orderIndex = #{orderIndex} 
        			
		  
             AND CI.title = #{title} 
        			
		  
             AND CI.valid = #{valid} 
        			
		  
             AND CI.createDate >= #{minCreateDate} 
        
		  
             AND CI.createDate < #{maxCreateDate} 
        
		  
             AND CI.createOperatorId = #{createOperatorId} 
        			
		  
             AND CI.remark = #{remark} 
        			
		  
             AND CI.content = #{content} 
        			
		
			  
	             AND CI.categoryCode = #{category.code} 
	        
        			
		  
             AND CI.keywords = #{keywords} 
        			
		
			 AND CI.id <> #{excludeId}
