mapper.select:
SELECT CIT.id, CIT.code, CIT.lastUpdateDate, CIT.lastUpdateOperatorId, CIT.name, CIT.valid, CIT.createDate, CIT.createOperatorId, CIT.modifyAble, CIT.remark FROM CI_CONTENT_TYPE CIT WHERE
AND CIT.id = #{id}
AND CIT.code = #{code}

	SELECT 
			CIT.id,
			CIT.code,
			CIT.lastUpdateDate,
			CIT.lastUpdateOperatorId,
			CIT.name,
			CIT.valid,
			CIT.createDate,
			CIT.createOperatorId,
			CIT.modifyAble,
			CIT.remark
	  FROM CI_CONTENT_TYPE CIT
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND CIT.id = #{id} 
        			
		  
             AND CIT.code = #{code} 
        			
		  
             AND CIT.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND CIT.name = #{name} 
        			
		  
             AND CIT.valid = #{valid} 
        			
		  
             AND CIT.createDate >= #{minCreateDate} 
        
		  
             AND CIT.createDate < #{maxCreateDate} 
        
		  
             AND CIT.createOperatorId = #{createOperatorId} 
        			
		  
             AND CIT.modifyAble = #{modifyAble} 
        			
		  
             AND CIT.remark = #{remark} 
        			
		
			 AND CIT.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM CI_CONTENT_TYPE CIT
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND CIT.id = #{id} 
        			
		  
             AND CIT.code = #{code} 
        			
		  
             AND CIT.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND CIT.name = #{name} 
        			
		  
             AND CIT.valid = #{valid} 
        			
		  
             AND CIT.createDate >= #{minCreateDate} 
        
		  
             AND CIT.createDate < #{maxCreateDate} 
        
		  
             AND CIT.createOperatorId = #{createOperatorId} 
        			
		  
             AND CIT.modifyAble = #{modifyAble} 
        			
		  
             AND CIT.remark = #{remark} 
        			
		
			 AND CIT.id <> #{excludeId}

mapper.delete:
	DELETE FROM CI_CONTENT_TYPE  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
