mapper.select:
SELECT TMN.id, TMN.code, TMN.lastUpdateDate, TMN.name, TMN.testInt, TMN.testLong, TMN.createDate, TMN.remark, TMN.attributes, TMN.description FROM TEST_TESTNESTEDMODE TMN WHERE
AND TMN.id = #{id}
AND TMN.code = #{code}

	SELECT 
			TMN.id,
			TMN.code,
			TMN.lastUpdateDate,
			TMN.name,
			TMN.testInt,
			TMN.testLong,
			TMN.createDate,
			TMN.remark,
			TMN.attributes,
			TMN.description
	  FROM TEST_TESTNESTEDMODE TMN
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND TMN.id = #{id} 
        			
		  
             AND TMN.code = #{code} 
        			
		  
             AND TMN.name = #{name} 
        			
		  
             AND TMN.testInt = #{testInt} 
        			
		  
             AND TMN.testLong = #{testLong} 
        			
		  
             AND TMN.createDate >= #{minCreateDate} 
        
		  
             AND TMN.createDate < #{maxCreateDate} 
        
		  
             AND TMN.remark = #{remark} 
        			
		  
             AND TMN.attributes = #{attributes} 
        			
		  
             AND TMN.description = #{description} 
        			
		
			 AND TMN.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM TEST_TESTNESTEDMODE TMN
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND TMN.id = #{id} 
        			
		  
             AND TMN.code = #{code} 
        			
		  
             AND TMN.name = #{name} 
        			
		  
             AND TMN.testInt = #{testInt} 
        			
		  
             AND TMN.testLong = #{testLong} 
        			
		  
             AND TMN.createDate >= #{minCreateDate} 
        
		  
             AND TMN.createDate < #{maxCreateDate} 
        
		  
             AND TMN.remark = #{remark} 
        			
		  
             AND TMN.attributes = #{attributes} 
        			
		  
             AND TMN.description = #{description} 
        			
		
			 AND TMN.id <> #{excludeId}

mapper.delete:
	DELETE FROM TEST_TESTNESTEDMODE  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
