mapper.select:
	SELECT 
			IC.id,
			IC.type,
			IC.vcid,
			IC.remark,
			IC.institutionId
	  FROM INS_INSTITUTION_CAPACITY IC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND IC.id = #{id} 
        			
		  
             AND IC.type = #{type} 
        			
		  
             AND IC.vcid = #{vcid} 
        			
		  
             AND IC.remark = #{remark} 
        			
		  
             AND IC.institutionId = #{institutionId} 
        			
		
			 AND IC.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY id DESC

	SELECT COUNT(1)
	  FROM INS_INSTITUTION_CAPACITY IC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND IC.id = #{id} 
        			
		  
             AND IC.type = #{type} 
        			
		  
             AND IC.vcid = #{vcid} 
        			
		  
             AND IC.remark = #{remark} 
        			
		  
             AND IC.institutionId = #{institutionId} 
        			
		
			 AND IC.id <> #{excludeId}
