mapper.select:
SELECT MC.id, MC.code, MC.lastUpdateDate, MC.name, MC.vcid, MC.valid, MC.createDate, MC.modifyAble, MC.parentId, MC.remark, MC.messageType FROM MSG_MESSAGE_CATALOG MC WHERE
AND MC.id = #{id}
AND MC.code = #{code}

	SELECT 
			MC.id,
			MC.code,
			MC.lastUpdateDate,
			MC.name,
			MC.vcid,
			MC.valid,
			MC.createDate,
			MC.modifyAble,
			MC.parentId,
			MC.remark,
			MC.messageType
	  FROM MSG_MESSAGE_CATALOG MC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND MC.parentId IN #{parentIdTemp}
        
		  
             AND MC.id = #{id} 
        			
		  
             AND MC.code = #{code} 
        			
		  
             AND MC.name = #{name} 
        			
		  
             AND MC.vcid = #{vcid} 
        			
		  
             AND MC.valid = #{valid} 
        			
		  
             AND MC.createDate >= #{minCreateDate} 
        
		  
             AND MC.createDate < #{maxCreateDate} 
        
		  
             AND MC.modifyAble = #{modifyAble} 
        			
		  
             AND MC.parentId = #{parentId} 
        			
		  
             AND MC.remark = #{remark} 
        			
		  
             AND MC.messageType = #{messageType} 
        			
		
			 AND MC.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM MSG_MESSAGE_CATALOG MC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND MC.parentId IN #{parentIdTemp}
        
		  
             AND MC.id = #{id} 
        			
		  
             AND MC.code = #{code} 
        			
		  
             AND MC.name = #{name} 
        			
		  
             AND MC.vcid = #{vcid} 
        			
		  
             AND MC.valid = #{valid} 
        			
		  
             AND MC.createDate >= #{minCreateDate} 
        
		  
             AND MC.createDate < #{maxCreateDate} 
        
		  
             AND MC.modifyAble = #{modifyAble} 
        			
		  
             AND MC.parentId = #{parentId} 
        			
		  
             AND MC.remark = #{remark} 
        			
		  
             AND MC.messageType = #{messageType} 
        			
		
			 AND MC.id <> #{excludeId}

mapper.delete:
	DELETE FROM MSG_MESSAGE_CATALOG  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
