mapper.select:
	SELECT 
			NU.id,
			NU.readDate,
			NU.userId,
			NU.userType,
			NU.noticeId,
			NU.unread
	  FROM MSG_NOTICE_2_USER NU
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND NU.id = #{id} 
        			
		  
             AND NU.userId = #{userId} 
        			
		  
             AND NU.userType = #{userType} 
        			
		  
             AND NU.noticeId = #{noticeId} 
        			
		  
             AND NU.unread = #{unread} 
        			
		
			 AND NU.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY id DESC

	SELECT COUNT(1)
	  FROM MSG_NOTICE_2_USER NU
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND NU.id = #{id} 
        			
		  
             AND NU.userId = #{userId} 
        			
		  
             AND NU.userType = #{userType} 
        			
		  
             AND NU.noticeId = #{noticeId} 
        			
		  
             AND NU.unread = #{unread} 
        			
		
			 AND NU.id <> #{excludeId}
