mapper.select:
SELECT NC.id, NC.code, NC.lastUpdateDate, NC.name, NC.vcid, NC.valid, NC.createDate, NC.modifyAble, NC.remark FROM MSG_NOTICE_CATALOG NC WHERE
AND NC.id = #{id}
AND NC.code = #{code}

	SELECT 
			NC.id,
			NC.code,
			NC.lastUpdateDate,
			NC.name,
			NC.vcid,
			NC.valid,
			NC.createDate,
			NC.modifyAble,
			NC.remark
	  FROM MSG_NOTICE_CATALOG NC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND NC.id = #{id} 
        			
		  
             AND NC.code = #{code} 
        			
		  
             AND NC.name = #{name} 
        			
		  
             AND NC.vcid = #{vcid} 
        			
		  
             AND NC.valid = #{valid} 
        			
		  
             AND NC.createDate >= #{minCreateDate} 
        
		  
             AND NC.createDate < #{maxCreateDate} 
        
		  
             AND NC.modifyAble = #{modifyAble} 
        			
		  
             AND NC.remark = #{remark} 
        			
		
			 AND NC.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM MSG_NOTICE_CATALOG NC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND NC.id = #{id} 
        			
		  
             AND NC.code = #{code} 
        			
		  
             AND NC.name = #{name} 
        			
		  
             AND NC.vcid = #{vcid} 
        			
		  
             AND NC.valid = #{valid} 
        			
		  
             AND NC.createDate >= #{minCreateDate} 
        
		  
             AND NC.createDate < #{maxCreateDate} 
        
		  
             AND NC.modifyAble = #{modifyAble} 
        			
		  
             AND NC.remark = #{remark} 
        			
		
			 AND NC.id <> #{excludeId}

mapper.delete:
	DELETE FROM MSG_NOTICE_CATALOG  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
