mapper.select:
	SELECT 
			NM.id,
			NM.lastUpdateDate,
			NM.lastUpdateUserId,
			NM.vcid,
			NM.title,
			NM.valid,
			NM.createDate,
			NM.createUserId,
			NM.sender,
			NM.catalogId,
			NM.content,
			NM.invalidDate,
			NM.publishDate,
			NM.priority,
			NM.published,
			NM.attributes
	  FROM MSG_NOTICE_MESSAGE NM
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND NM.id = #{id} 
        			
		  
             AND NM.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND NM.vcid = #{vcid} 
        			
		  
             AND NM.title = #{title} 
        			
		  
             AND NM.valid = #{valid} 
        			
		  
             AND NM.createDate >= #{minCreateDate} 
        
		  
             AND NM.createDate < #{maxCreateDate} 
        
		  
             AND NM.createUserId = #{createUserId} 
        			
		  
             AND NM.sender = #{sender} 
        			
		
			  
	             AND NM.catalogId = #{catalog.id} 
	        
        			
		  
             AND NM.content = #{content} 
        			
		  
             AND NM.priority = #{priority} 
        			
		  
             AND NM.published = #{published} 
        			
		  
             AND NM.attributes = #{attributes} 
        			
		
			 AND NM.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM MSG_NOTICE_MESSAGE NM
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND NM.id = #{id} 
        			
		  
             AND NM.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND NM.vcid = #{vcid} 
        			
		  
             AND NM.title = #{title} 
        			
		  
             AND NM.valid = #{valid} 
        			
		  
             AND NM.createDate >= #{minCreateDate} 
        
		  
             AND NM.createDate < #{maxCreateDate} 
        
		  
             AND NM.createUserId = #{createUserId} 
        			
		  
             AND NM.sender = #{sender} 
        			
		
			  
	             AND NM.catalogId = #{catalog.id} 
	        
        			
		  
             AND NM.content = #{content} 
        			
		  
             AND NM.priority = #{priority} 
        			
		  
             AND NM.published = #{published} 
        			
		  
             AND NM.attributes = #{attributes} 
        			
		
			 AND NM.id <> #{excludeId}
