mapper.select:
	SELECT 
			PM.id,
			PM.lastUpdateDate,
			PM.lastUpdateUserId,
			PM.readDate,
			PM.type,
			PM.userId,
			PM.userType,
			PM.vcid,
			PM.title,
			PM.createDate,
			PM.createUserId,
			PM.senderUsername,
			PM.senderUserId,
			PM.senderUserType,
			PM.unread,
			PM.content,
			PM.username
	  FROM MSG_PRIVATE_MESSAGE PM
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND PM.id = #{id} 
        			
		  
             AND PM.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND PM.type = #{type} 
        			
		  
             AND PM.userId = #{userId} 
        			
		  
             AND PM.userType = #{userType} 
        			
		  
             AND PM.vcid = #{vcid} 
        			
		  
             AND PM.title = #{title} 
        			
		  
             AND PM.createDate >= #{minCreateDate} 
        
		  
             AND PM.createDate < #{maxCreateDate} 
        
		  
             AND PM.createUserId = #{createUserId} 
        			
		  
             AND PM.senderUsername = #{senderUsername} 
        			
		  
             AND PM.senderUserId = #{senderUserId} 
        			
		  
             AND PM.senderUserType = #{senderUserType} 
        			
		  
             AND PM.unread = #{unread} 
        			
		  
             AND PM.content = #{content} 
        			
		  
             AND PM.username = #{username} 
        			
		
			 AND PM.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM MSG_PRIVATE_MESSAGE PM
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND PM.id = #{id} 
        			
		  
             AND PM.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND PM.type = #{type} 
        			
		  
             AND PM.userId = #{userId} 
        			
		  
             AND PM.userType = #{userType} 
        			
		  
             AND PM.vcid = #{vcid} 
        			
		  
             AND PM.title = #{title} 
        			
		  
             AND PM.createDate >= #{minCreateDate} 
        
		  
             AND PM.createDate < #{maxCreateDate} 
        
		  
             AND PM.createUserId = #{createUserId} 
        			
		  
             AND PM.senderUsername = #{senderUsername} 
        			
		  
             AND PM.senderUserId = #{senderUserId} 
        			
		  
             AND PM.senderUserType = #{senderUserType} 
        			
		  
             AND PM.unread = #{unread} 
        			
		  
             AND PM.content = #{content} 
        			
		  
             AND PM.username = #{username} 
        			
		
			 AND PM.id <> #{excludeId}
