mapper.select:
	SELECT 
			NC.id,
			NC.lastUpdateDate,
			NC.lastUpdateUserId,
			NC.name,
			NC.type,
			NC.topicId,
			NC.topicType,
			NC.createDate,
			NC.createUserId,
			NC.parentId,
			NC.remark
	  FROM NOTE_NOTEPAD_CATALOG NC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND NC.parentId IN #{parentIdTemp}
        
		  
             AND NC.id = #{id} 
        			
		  
             AND NC.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND NC.name = #{name} 
        			
		  
             AND NC.type = #{type} 
        			
		  
             AND NC.topicId = #{topicId} 
        			
		  
             AND NC.topicType = #{topicType} 
        			
		  
             AND NC.createDate >= #{minCreateDate} 
        
		  
             AND NC.createDate < #{maxCreateDate} 
        
		  
             AND NC.createUserId = #{createUserId} 
        			
		  
             AND NC.parentId = #{parentId} 
        			
		  
             AND NC.remark = #{remark} 
        			
		
			 AND NC.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM NOTE_NOTEPAD_CATALOG NC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND NC.parentId IN #{parentIdTemp}
        
		  
             AND NC.id = #{id} 
        			
		  
             AND NC.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND NC.name = #{name} 
        			
		  
             AND NC.type = #{type} 
        			
		  
             AND NC.topicId = #{topicId} 
        			
		  
             AND NC.topicType = #{topicType} 
        			
		  
             AND NC.createDate >= #{minCreateDate} 
        
		  
             AND NC.createDate < #{maxCreateDate} 
        
		  
             AND NC.createUserId = #{createUserId} 
        			
		  
             AND NC.parentId = #{parentId} 
        			
		  
             AND NC.remark = #{remark} 
        			
		
			 AND NC.id <> #{excludeId}
