mapper.select:
SELECT N.id, N.vcid, N.type, N.title, N.topicId, N.topicType, N.remark, N.catalogId, N.content, N.attributes, N.lastUpdateDate, N.lastUpdateUserId, N.createDate, N.createUserId FROM NOTE_NOTEPAD N WHERE
AND N.id = #{id}
AND N.vcid = #{vcid}
AND N.type = #{type}
AND N.topicId = #{topicId}
AND N.topicType = #{topicType}

	SELECT 
			N.id,
			N.vcid,
			N.type,
			N.title,
			N.topicId,
			N.topicType,
			N.remark,
			N.catalogId,
			N.content,
			N.attributes,
			N.lastUpdateDate,
			N.lastUpdateUserId,
			N.createDate,
			N.createUserId
	  FROM NOTE_NOTEPAD N
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND N.id = #{id} 
        
          
             AND N.vcid = #{vcid} 
        			
		  
             AND N.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND N.type = #{type} 
        			
		  
             AND N.title = #{title} 
        			
		  
             AND N.topicId = #{topicId} 
        			
		  
             AND N.topicType = #{topicType} 
        			
		  
             AND N.createDate >= #{minCreateDate} 
        
		  
             AND N.createDate < #{maxCreateDate} 
        
		  
             AND N.createUserId = #{createUserId} 
        			
		  
             AND N.remark = #{remark} 
        			
		
			  
	             AND N.catalogId = #{catalog.id} 
	        
        			
		  
             AND N.content = #{content} 
        			
		  
             AND N.attributes = #{attributes} 
        			
		
			 AND N.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM NOTE_NOTEPAD N
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND N.id = #{id} 
        
          
             AND N.vcid = #{vcid} 
        			
		  
             AND N.lastUpdateUserId = #{lastUpdateUserId} 
        			
		  
             AND N.type = #{type} 
        			
		  
             AND N.title = #{title} 
        			
		  
             AND N.topicId = #{topicId} 
        			
		  
             AND N.topicType = #{topicType} 
        			
		  
             AND N.createDate >= #{minCreateDate} 
        
		  
             AND N.createDate < #{maxCreateDate} 
        
		  
             AND N.createUserId = #{createUserId} 
        			
		  
             AND N.remark = #{remark} 
        			
		
			  
	             AND N.catalogId = #{catalog.id} 
	        
        			
		  
             AND N.content = #{content} 
        			
		  
             AND N.attributes = #{attributes} 
        			
		
			 AND N.id <> #{excludeId}

mapper.delete:
	DELETE FROM NOTE_NOTEPAD  WHERE
	
		  
            AND id = #{id}
        
          
             AND type = #{type} 
        
          
             AND topicId = #{topicId} 
        			
		  
             AND topicType = #{topicType} 
        
          
             AND vcid = #{vcid}

mapper.update:
  
	    UPDATE NOTE_NOTEPAD
	    
	    	
			
			
			
			
			
			
			
			
			
			
			
			
	    		lastUpdateDate = #{lastUpdateDate,javaType=java.util.Date},
	    	
			
	    		lastUpdateUserId = #{lastUpdateUserId,javaType=java.lang.String},
	    	
			
	    		title = #{title,javaType=java.lang.String},
	    	
			
	    		remark = #{remark,javaType=java.lang.String},
	    	
			
	    		catalogId = #{catalog.id,javaType=java.lang.String},
	    	
			
	    		content = #{content,javaType=java.lang.String},
	    	
			
	    		attributes = #{attributes,javaType=java.lang.String},
	    	
	    
	    WHERE
		
			  
	            AND id = #{id}
	        
	          
	             AND type = #{type} 
	        
	          
	             AND topicId = #{topicId} 
	        			
			  
	             AND topicType = #{topicType} 
	        
	          
	             AND vcid = #{vcid}
