mapper.select:
SELECT EI.id, EI.code, EI.idCardType, EI.idCardNumber, EI.age, EI.sex, EI.lastUpdateDate, EI.name, EI.email, EI.entryDate, EI.phoneNumber, EI.createDate, EI.leaving, EI.leavingDate, EI.birthday, EI.official, EI.officialDate, EI.operatorId FROM OPER_EMPLOYEE_INFO EI WHERE
AND EI.id = #{id}
AND EI.code = #{code}

	SELECT 
			EI.id,
			EI.code,
			EI.idCardType,
			EI.idCardNumber,
			EI.age,
			EI.sex,
			EI.lastUpdateDate,
			EI.name,
			EI.email,
			EI.entryDate,
			EI.phoneNumber,
			EI.createDate,
			EI.leaving,
			EI.leavingDate,
			EI.birthday,
			EI.official,
			EI.officialDate,
			EI.operatorId
	  FROM OPER_EMPLOYEE_INFO EI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND EI.id = #{id} 
        			
		  
             AND EI.code = #{code} 
        			
		  
             AND EI.idCardType = #{idCardType} 
        			
		  
             AND EI.idCardNumber = #{idCardNumber} 
        			
		  
             AND EI.age = #{age} 
        			
		  
             AND EI.sex = #{sex} 
        			
		  
             AND EI.name = #{name} 
        			
		  
             AND EI.email = #{email} 
        			
		  
             AND EI.phoneNumber = #{phoneNumber} 
        			
		  
             AND EI.createDate >= #{minCreateDate} 
        
		  
             AND EI.createDate < #{maxCreateDate} 
        
		  
             AND EI.leaving = #{leaving} 
        			
		  
             AND EI.official = #{official} 
        			
		
			  
	             AND EI.operatorId = #{operator.id} 
	        
        			
		
			 AND EI.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM OPER_EMPLOYEE_INFO EI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND EI.id = #{id} 
        			
		  
             AND EI.code = #{code} 
        			
		  
             AND EI.idCardType = #{idCardType} 
        			
		  
             AND EI.idCardNumber = #{idCardNumber} 
        			
		  
             AND EI.age = #{age} 
        			
		  
             AND EI.sex = #{sex} 
        			
		  
             AND EI.name = #{name} 
        			
		  
             AND EI.email = #{email} 
        			
		  
             AND EI.phoneNumber = #{phoneNumber} 
        			
		  
             AND EI.createDate >= #{minCreateDate} 
        
		  
             AND EI.createDate < #{maxCreateDate} 
        
		  
             AND EI.leaving = #{leaving} 
        			
		  
             AND EI.official = #{official} 
        			
		
			  
	             AND EI.operatorId = #{operator.id} 
	        
        			
		
			 AND EI.id <> #{excludeId}

mapper.delete:
	DELETE FROM OPER_EMPLOYEE_INFO  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
