mapper.select:
	SELECT 
			EI.id,
			EI.idCardType,
			EI.idCardNumber,
			EI.sex,
			EI.lastUpdateDate,
			EI.realName,
			EI.email,
			EI.entryDate,
			EI.createDate,
			EI.mobileNumber,
			EI.number,
			EI.leaving,
			EI.leavingDate,
			EI.birthday,
			EI.official,
			EI.officialDate,
			EI.operatorId
	  FROM OPER_EMPLOYEE_INFO EI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND EI.id = #{id} 
        			
		  
             AND EI.idCardType = #{idCardType} 
        			
		  
             AND EI.idCardNumber = #{idCardNumber} 
        			
		  
             AND EI.sex = #{sex} 
        			
		  
             AND EI.realName = #{realName} 
        			
		  
             AND EI.email = #{email} 
        			
		  
             AND EI.createDate >= #{minCreateDate} 
        
		  
             AND EI.createDate < #{maxCreateDate} 
        
		  
             AND EI.mobileNumber = #{mobileNumber} 
        			
		  
             AND EI.number = #{number} 
        			
		  
             AND EI.leaving = #{leaving} 
        			
		  
             AND EI.official = #{official} 
        			
		
			  
	             AND EI.operatorId = #{operator.id} 
	        
        			
		
			 AND EI.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM OPER_EMPLOYEE_INFO EI
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND EI.id = #{id} 
        			
		  
             AND EI.idCardType = #{idCardType} 
        			
		  
             AND EI.idCardNumber = #{idCardNumber} 
        			
		  
             AND EI.sex = #{sex} 
        			
		  
             AND EI.realName = #{realName} 
        			
		  
             AND EI.email = #{email} 
        			
		  
             AND EI.createDate >= #{minCreateDate} 
        
		  
             AND EI.createDate < #{maxCreateDate} 
        
		  
             AND EI.mobileNumber = #{mobileNumber} 
        			
		  
             AND EI.number = #{number} 
        			
		  
             AND EI.leaving = #{leaving} 
        			
		  
             AND EI.official = #{official} 
        			
		
			  
	             AND EI.operatorId = #{operator.id} 
	        
        			
		
			 AND EI.id <> #{excludeId}
