mapper.select:
	SELECT 
			OSA.id,
			OSA.idCardType,
			OSA.idCardNumber,
			OSA.idCardExpiryDate,
			OSA.lastUpdateDate,
			OSA.realNameAuthenticated,
			OSA.realNameErrCount,
			OSA.realNameLastErrDate,
			OSA.email,
			OSA.emailBinding,
			OSA.createDate,
			OSA.mobileNumber,
			OSA.mobileBinding,
			OSA.operatorId
	  FROM OPER_SECURITY_ACCOUNT OSA
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND OSA.id = #{id} 
        			
		  
             AND OSA.idCardType = #{idCardType} 
        			
		  
             AND OSA.idCardNumber = #{idCardNumber} 
        			
		  
             AND OSA.realNameAuthenticated = #{realNameAuthenticated} 
        			
		  
             AND OSA.realNameErrCount = #{realNameErrCount} 
        			
		  
             AND OSA.email = #{email} 
        			
		  
             AND OSA.emailBinding = #{emailBinding} 
        			
		  
             AND OSA.createDate >= #{minCreateDate} 
        
		  
             AND OSA.createDate < #{maxCreateDate} 
        
		  
             AND OSA.mobileNumber = #{mobileNumber} 
        			
		  
             AND OSA.mobileBinding = #{mobileBinding} 
        			
		  
             AND OSA.operatorId = #{operatorId} 
        			
		
			 AND OSA.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM OPER_SECURITY_ACCOUNT OSA
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND OSA.id = #{id} 
        			
		  
             AND OSA.idCardType = #{idCardType} 
        			
		  
             AND OSA.idCardNumber = #{idCardNumber} 
        			
		  
             AND OSA.realNameAuthenticated = #{realNameAuthenticated} 
        			
		  
             AND OSA.realNameErrCount = #{realNameErrCount} 
        			
		  
             AND OSA.email = #{email} 
        			
		  
             AND OSA.emailBinding = #{emailBinding} 
        			
		  
             AND OSA.createDate >= #{minCreateDate} 
        
		  
             AND OSA.createDate < #{maxCreateDate} 
        
		  
             AND OSA.mobileNumber = #{mobileNumber} 
        			
		  
             AND OSA.mobileBinding = #{mobileBinding} 
        			
		  
             AND OSA.operatorId = #{operatorId} 
        			
		
			 AND OSA.id <> #{excludeId}
