mapper.select:
SELECT OP.id, OP.code, OP.fullName, OP.name, OP.vcid, OP.valid, OP.parentId, OP.remark, OP.organizationId FROM OPER_POST OP WHERE
AND OP.id = #{id}
AND OP.code = #{code}

	SELECT 
			OP.id,
			OP.code,
			OP.fullName,
			OP.name,
			OP.vcid,
			OP.valid,
			OP.parentId,
			OP.remark,
			OP.organizationId
	  FROM OPER_POST OP
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND OP.parentId IN #{parentIdTemp}
        
		  
             AND OP.id = #{id} 
        			
		  
             AND OP.code = #{code} 
        			
		  
             AND OP.fullName = #{fullName} 
        			
		  
             AND OP.name = #{name} 
        			
		  
             AND OP.vcid = #{vcid} 
        			
		  
             AND OP.valid = #{valid} 
        			
		  
             AND OP.parentId = #{parentId} 
        			
		  
             AND OP.remark = #{remark} 
        			
		
			  
	             AND OP.organizationId = #{organization.id} 
	        
        			
		
			 AND OP.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY id DESC

	SELECT COUNT(1)
	  FROM OPER_POST OP
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND OP.parentId IN #{parentIdTemp}
        
		  
             AND OP.id = #{id} 
        			
		  
             AND OP.code = #{code} 
        			
		  
             AND OP.fullName = #{fullName} 
        			
		  
             AND OP.name = #{name} 
        			
		  
             AND OP.vcid = #{vcid} 
        			
		  
             AND OP.valid = #{valid} 
        			
		  
             AND OP.parentId = #{parentId} 
        			
		  
             AND OP.remark = #{remark} 
        			
		
			  
	             AND OP.organizationId = #{organization.id} 
	        
        			
		
			 AND OP.id <> #{excludeId}

mapper.delete:
	DELETE FROM OPER_POST  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
