mapper.select:
	SELECT 
			OR.id,
			OR.refId,
			OR.refType,
			OR.lastUpdateDate,
			OR.lastUpdateOperatorId,
			OR.createDate,
			OR.createOperatorId,
			OR.expiryDate,
			OR.operatorId,
			OR.effectiveDate
	  FROM OPER_OPERATOR_REF OR
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND OR.id = #{id} 
        			
		  
             AND OR.refId = #{refId} 
        			
		  
             AND OR.refType = #{refType} 
        			
		  
             AND OR.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND OR.createDate >= #{minCreateDate} 
        
		  
             AND OR.createDate < #{maxCreateDate} 
        
		  
             AND OR.createOperatorId = #{createOperatorId} 
        			
		  
             AND OR.operatorId = #{operatorId} 
        			
		
			 AND OR.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM OPER_OPERATOR_REF OR
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND OR.id = #{id} 
        			
		  
             AND OR.refId = #{refId} 
        			
		  
             AND OR.refType = #{refType} 
        			
		  
             AND OR.lastUpdateOperatorId = #{lastUpdateOperatorId} 
        			
		  
             AND OR.createDate >= #{minCreateDate} 
        
		  
             AND OR.createDate < #{maxCreateDate} 
        
		  
             AND OR.createOperatorId = #{createOperatorId} 
        			
		  
             AND OR.operatorId = #{operatorId} 
        			
		
			 AND OR.id <> #{excludeId}
