mapper.select:
	SELECT 
			R.id,
			R.parentId,
			R.roleTypeId,
			R.name,
			R.vcid,
			R.valid,
			R.modifyAble,
			R.remark,
			R.createDate,
			R.lastUpdateDate
	  FROM OPER_ROLE R
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND R.id = #{id} 
        
          
            AND R.parentId IN #{parentIdTemp}
        
          
             AND R.parentId = #{parentId} 
        
          
             AND R.roleTypeId = #{roleTypeId} 
        
          
             AND R.createDate >= #{minCreateDate} 
        
		  
             AND R.createDate < #{maxCreateDate} 
        
		  
             AND R.name = #{name} 
        			
		  
             AND R.vcid = #{vcid} 
        			
		  
             AND R.valid = #{valid} 
        			
		  
             AND R.modifyAble = #{modifyAble} 
        
		
			 AND R.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY id DESC

	SELECT COUNT(1)
	  FROM OPER_ROLE R
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND R.id = #{id} 
        
          
            AND R.parentId IN #{parentIdTemp}
        
          
             AND R.parentId = #{parentId} 
        
          
             AND R.roleTypeId = #{roleTypeId} 
        
          
             AND R.createDate >= #{minCreateDate} 
        
		  
             AND R.createDate < #{maxCreateDate} 
        
		  
             AND R.name = #{name} 
        			
		  
             AND R.vcid = #{vcid} 
        			
		  
             AND R.valid = #{valid} 
        			
		  
             AND R.modifyAble = #{modifyAble} 
        
		
			 AND R.id <> #{excludeId}
