mapper.select:
SELECT O.id, O.pwdErrCount, O.pwdUpdateDate, O.lastUpdateDate, O.mainPostId, O.name, O.vcid, O.valid, O.createDate, O.locked, O.modifyAble, O.examinePwd, O.historyPwd, O.invalidDate, O.password, O.username, O.usernameChangeCount, O.organizationId FROM OPER_OPERATOR O WHERE
AND O.id = #{id}
AND O.username = #{username}

	SELECT 
			O.id,
			O.pwdErrCount,
			O.pwdUpdateDate,
			O.lastUpdateDate,
			O.mainPostId,
			O.name,
			O.vcid,
			O.valid,
			O.createDate,
			O.locked,
			O.modifyAble,
			O.examinePwd,
			O.historyPwd,
			O.invalidDate,
			O.password,
			O.username,
			O.usernameChangeCount,
			O.organizationId
	  FROM OPER_OPERATOR O
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND O.id = #{id} 
        			
		  
             AND O.pwdErrCount = #{pwdErrCount} 
        			
		  
             AND O.mainPostId = #{mainPostId} 
        			
		  
             AND O.name = #{name} 
        			
		  
             AND O.vcid = #{vcid} 
        			
		  
             AND O.valid = #{valid} 
        			
		  
             AND O.createDate >= #{minCreateDate} 
        
		  
             AND O.createDate < #{maxCreateDate} 
        
		  
             AND O.locked = #{locked} 
        			
		  
             AND O.modifyAble = #{modifyAble} 
        			
		  
             AND O.examinePwd = #{examinePwd} 
        			
		  
             AND O.historyPwd = #{historyPwd} 
        			
		  
             AND O.password = #{password} 
        			
		  
             AND O.username = #{username} 
        			
		  
             AND O.organizationId = #{organizationId} 
        			
		
			 AND O.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM OPER_OPERATOR O
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND O.id = #{id} 
        			
		  
             AND O.pwdErrCount = #{pwdErrCount} 
        			
		  
             AND O.mainPostId = #{mainPostId} 
        			
		  
             AND O.name = #{name} 
        			
		  
             AND O.vcid = #{vcid} 
        			
		  
             AND O.valid = #{valid} 
        			
		  
             AND O.createDate >= #{minCreateDate} 
        
		  
             AND O.createDate < #{maxCreateDate} 
        
		  
             AND O.locked = #{locked} 
        			
		  
             AND O.modifyAble = #{modifyAble} 
        			
		  
             AND O.examinePwd = #{examinePwd} 
        			
		  
             AND O.historyPwd = #{historyPwd} 
        			
		  
             AND O.password = #{password} 
        			
		  
             AND O.username = #{username} 
        			
		  
             AND O.organizationId = #{organizationId} 
        			
		
			 AND O.id <> #{excludeId}
