mapper.select:
	SELECT 
			O.id,
			O.pwdErrCount,
			O.pwdUpdateDate,
			O.lastUpdateDate,
			O.mainPostId,
			O.userName,
			O.vcid,
			O.loginName,
			O.valid,
			O.createDate,
			O.locked,
			O.examinePwd,
			O.historyPwd,
			O.invalidDate,
			O.password,
			O.organizationId
	  FROM OPER_OPERATOR O
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND O.id = #{id} 
        			
		  
             AND O.pwdErrCount = #{pwdErrCount} 
        			
		
			  
	             AND O.mainPostId = #{mainPost.id} 
	        
        			
		  
             AND O.userName = #{userName} 
        			
		  
             AND O.vcid = #{vcid} 
        			
		  
             AND O.loginName = #{loginName} 
        			
		  
             AND O.valid = #{valid} 
        			
		  
             AND O.createDate >= #{minCreateDate} 
        
		  
             AND O.createDate < #{maxCreateDate} 
        
		  
             AND O.locked = #{locked} 
        			
		  
             AND O.examinePwd = #{examinePwd} 
        			
		  
             AND O.historyPwd = #{historyPwd} 
        			
		  
             AND O.password = #{password} 
        			
		
			  
	             AND O.organizationId = #{organization.id} 
	        
        			
		
			 AND O.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM OPER_OPERATOR O
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
             AND O.id = #{id} 
        			
		  
             AND O.pwdErrCount = #{pwdErrCount} 
        			
		
			  
	             AND O.mainPostId = #{mainPost.id} 
	        
        			
		  
             AND O.userName = #{userName} 
        			
		  
             AND O.vcid = #{vcid} 
        			
		  
             AND O.loginName = #{loginName} 
        			
		  
             AND O.valid = #{valid} 
        			
		  
             AND O.createDate >= #{minCreateDate} 
        
		  
             AND O.createDate < #{maxCreateDate} 
        
		  
             AND O.locked = #{locked} 
        			
		  
             AND O.examinePwd = #{examinePwd} 
        			
		  
             AND O.historyPwd = #{historyPwd} 
        			
		  
             AND O.password = #{password} 
        			
		
			  
	             AND O.organizationId = #{organization.id} 
	        
        			
		
			 AND O.id <> #{excludeId}
