mapper.select:
SELECT O.id, O.code, O.fullName, O.fullAddress, O.lastUpdateDate, O.name, O.type, O.vcid, O.alias, O.chiefId, O.chiefType, O.valid, O.createDate, O.parentId, O.remark, O.address, O.companyId, O.districtId, O.departmentId FROM OPER_ORGANIZATION O WHERE
AND O.id = #{id}
AND O.code = #{code}

	SELECT 
			O.id,
			O.code,
			O.fullName,
			O.fullAddress,
			O.lastUpdateDate,
			O.name,
			O.type,
			O.vcid,
			O.alias,
			O.chiefId,
			O.chiefType,
			O.valid,
			O.createDate,
			O.parentId,
			O.remark,
			O.address,
			O.companyId,
			O.districtId,
			O.departmentId
	  FROM OPER_ORGANIZATION O
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND O.parentId IN #{parentIdTemp}
        
		  
             AND O.id = #{id} 
        			
		  
             AND O.code = #{code} 
        			
		  
             AND O.fullName = #{fullName} 
        			
		  
             AND O.fullAddress = #{fullAddress} 
        			
		  
             AND O.name = #{name} 
        			
		  
             AND O.type = #{type} 
        			
		  
             AND O.vcid = #{vcid} 
        			
		  
             AND O.alias = #{alias} 
        			
		  
             AND O.chiefId = #{chiefId} 
        			
		  
             AND O.chiefType = #{chiefType} 
        			
		  
             AND O.valid = #{valid} 
        			
		  
             AND O.createDate >= #{minCreateDate} 
        
		  
             AND O.createDate < #{maxCreateDate} 
        
		  
             AND O.parentId = #{parentId} 
        			
		  
             AND O.remark = #{remark} 
        			
		  
             AND O.address = #{address} 
        			
		
			  
	             AND O.companyId = #{company.id} 
	        
        			
		  
             AND O.districtId = #{districtId} 
        			
		
			  
	             AND O.departmentId = #{department.id} 
	        
        			
		
			 AND O.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM OPER_ORGANIZATION O
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND O.parentId IN #{parentIdTemp}
        
		  
             AND O.id = #{id} 
        			
		  
             AND O.code = #{code} 
        			
		  
             AND O.fullName = #{fullName} 
        			
		  
             AND O.fullAddress = #{fullAddress} 
        			
		  
             AND O.name = #{name} 
        			
		  
             AND O.type = #{type} 
        			
		  
             AND O.vcid = #{vcid} 
        			
		  
             AND O.alias = #{alias} 
        			
		  
             AND O.chiefId = #{chiefId} 
        			
		  
             AND O.chiefType = #{chiefType} 
        			
		  
             AND O.valid = #{valid} 
        			
		  
             AND O.createDate >= #{minCreateDate} 
        
		  
             AND O.createDate < #{maxCreateDate} 
        
		  
             AND O.parentId = #{parentId} 
        			
		  
             AND O.remark = #{remark} 
        			
		  
             AND O.address = #{address} 
        			
		
			  
	             AND O.companyId = #{company.id} 
	        
        			
		  
             AND O.districtId = #{districtId} 
        			
		
			  
	             AND O.departmentId = #{department.id} 
	        
        			
		
			 AND O.id <> #{excludeId}

mapper.delete:
	DELETE FROM OPER_ORGANIZATION  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
