mapper.select:
SELECT P.id, P.code, P.fullName, P.name, P.vcid, P.valid, P.parentId, P.remark, P.organizationId FROM OPER_POST P WHERE
AND P.id = #{id}
AND P.code = #{code}

	SELECT 
			P.id,
			P.code,
			P.fullName,
			P.name,
			P.vcid,
			P.valid,
			P.parentId,
			P.remark,
			P.organizationId
	  FROM OPER_POST P
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND P.parentId IN #{parentIdTemp}
        
		  
             AND P.id = #{id} 
        			
		  
             AND P.code = #{code} 
        			
		  
             AND P.fullName = #{fullName} 
        			
		  
             AND P.name = #{name} 
        			
		  
             AND P.vcid = #{vcid} 
        			
		  
             AND P.valid = #{valid} 
        			
		  
             AND P.parentId = #{parentId} 
        			
		  
             AND P.remark = #{remark} 
        			
		
			  
	             AND P.organizationId = #{organization.id} 
	        
        			
		
			 AND P.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY id DESC

	SELECT COUNT(1)
	  FROM OPER_POST P
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND P.parentId IN #{parentIdTemp}
        
		  
             AND P.id = #{id} 
        			
		  
             AND P.code = #{code} 
        			
		  
             AND P.fullName = #{fullName} 
        			
		  
             AND P.name = #{name} 
        			
		  
             AND P.vcid = #{vcid} 
        			
		  
             AND P.valid = #{valid} 
        			
		  
             AND P.parentId = #{parentId} 
        			
		  
             AND P.remark = #{remark} 
        			
		
			  
	             AND P.organizationId = #{organization.id} 
	        
        			
		
			 AND P.id <> #{excludeId}

mapper.delete:
	DELETE FROM OPER_POST  WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
