mapper.select:
SELECT VC.id, VC.code, VC.name, VC.valid, VC.createDate, VC.lastUpdateDate, VC.modifyAble, VC.parentId, VC.remark FROM VC_VIRTUAL_CENTER VC WHERE
AND VC.id = #{id}
AND VC.code = #{code}

	SELECT 
			VC.id,
			VC.code,
			VC.name,
			VC.valid,
			VC.createDate,
			VC.lastUpdateDate,
			VC.modifyAble,
			VC.parentId,
			VC.remark
	  FROM VC_VIRTUAL_CENTER VC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND VC.parentId IN #{parentIdTemp}
        
		  
             AND VC.id = #{id} 
        			
		  
             AND VC.code = #{code} 
        			
		  
             AND VC.name = #{name} 
        			
		  
             AND VC.valid = #{valid} 
        			
		  
             AND VC.createDate >= #{minCreateDate} 
        
		  
             AND VC.createDate < #{maxCreateDate} 
        
		  
             AND VC.modifyAble = #{modifyAble} 
        			
		  
             AND VC.parentId = #{parentId} 
        			
		  
             AND VC.remark = #{remark} 
        			
		
			 AND VC.id <> #{excludeId} 
		
	
	  
          
            ORDER BY ${orderTemp.column} ${orderTemp.direction}
        
          
            ORDER BY createDate DESC

	SELECT COUNT(1)
	  FROM VC_VIRTUAL_CENTER VC
	
		
		
		  
	        
	        	AND ${conditionTemp.column} ${conditionTemp.operator}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{valueTemp}
	        
	          
	            AND ${conditionTemp.column} ${conditionTemp.operator} #{conditionTemp.value}
	        
		
		
        
		  
            AND VC.parentId IN #{parentIdTemp}
        
		  
             AND VC.id = #{id} 
        			
		  
             AND VC.code = #{code} 
        			
		  
             AND VC.name = #{name} 
        			
		  
             AND VC.valid = #{valid} 
        			
		  
             AND VC.createDate >= #{minCreateDate} 
        
		  
             AND VC.createDate < #{maxCreateDate} 
        
		  
             AND VC.modifyAble = #{modifyAble} 
        			
		  
             AND VC.parentId = #{parentId} 
        			
		  
             AND VC.remark = #{remark} 
        			
		
			 AND VC.id <> #{excludeId}

mapper.delete:
	DELETE FROM VC_VIRTUAL_CENTER WHERE
	
		  
            AND id = #{id}
        
		  
            AND code = #{code}
